package com.hummingbird.maaccount.exception;

/**
 * @author huangjiej_2
 * 2014年12月28日 上午11:26:22
 * 本类主要做为帐户余额不足异常
 */
public class InsufficientAccountBalanceException extends MaAccountException {

	public InsufficientAccountBalanceException() {
		super();
		// TODO Auto-generated constructor stub
	}

	public InsufficientAccountBalanceException(int errcode, String message) {
		super(errcode, message);
		// TODO Auto-generated constructor stub
	}

	public InsufficientAccountBalanceException(String message, Throwable cause) {
		super(message, cause);
		errcode=9001;
	}

	public InsufficientAccountBalanceException(String message) {
		super(message);
		errcode=9001;
	}

	public InsufficientAccountBalanceException(Throwable cause) {
		super("帐户余额不足",cause);
		errcode=9001;
		
	}

}
