package com.hummingbird.maaccount.vo;

/**
 * @author huangjiej_2
 * 2015年1月8日 下午10:15:48
 * 本类主要做为订单查询的主参数
 */
public class OrderQueryVO {

	/**
	 * 构造函数
	 */
	public OrderQueryVO() {
		// TODO Auto-generated constructor stub
	}
	
	protected AppVO app;
	
	protected OrderQueryPagingVO query;

	/**
	 * @return the app
	 */
	public AppVO getApp() {
		return app;
	}

	/**
	 * @param app the app to set
	 */
	public void setApp(AppVO app) {
		this.app = app;
	}

	/**
	 * @return the order
	 */
	public OrderQueryPagingVO getQuery() {
		return query;
	}

	/**
	 * @param order the order to set
	 */
	public void setQuery(OrderQueryPagingVO order) {
		this.query = order;
	}

	/* (non-Javadoc)
	 * @see java.lang.Object#toString()
	 */
	@Override
	public String toString() {
		return "OrderQueryVO [app=" + app + ", order=" + query + "]";
	}
	
	

}
